mapper.select:
select

from conf_message_mq

  and id = #{id,jdbcType=BIGINT}
  and exchange_name = #{msgCode,jdbcType=VARCHAR}
  and exchange_durable = #{exChange,jdbcType=VARCHAR}
  and exchange_auto_delete = #{routingKey,jdbcType=VARCHAR}
  and state = #{state,jdbcType=INTEGER}

order by id desc

mapper.insert:
insert into conf_message_mq (id, exchange_name, exchange_durable, 
  exchange_auto_delete, queue_name, queue_durable, 
  queue_auto_delete, queue_exclusive, routint_key, 
  crt_date, state, modify_date
  )
values (#{id,jdbcType=BIGINT}, #{exchangeName,jdbcType=VARCHAR}, #{exchangeDurable,jdbcType=TINYINT}, 
  #{exchangeAutoDelete,jdbcType=TINYINT}, #{queueName,jdbcType=VARCHAR}, #{queueDurable,jdbcType=TINYINT}, 
  #{queueAutoDelete,jdbcType=TINYINT}, #{queueExclusive,jdbcType=TINYINT}, #{routintKey,jdbcType=VARCHAR}, 
  #{crtDate,jdbcType=TIMESTAMP}, #{state,jdbcType=TINYINT}, #{modifyDate,jdbcType=TIMESTAMP}
  )
