mapper.select:
select

from tb_sys_mq_msg

  and msg_id = #{msgId,jdbcType=INTEGER}
  and msg_code = #{msgCode,jdbcType=VARCHAR}
  and ex_change = #{exChange,jdbcType=VARCHAR}
  and routing_key = #{routingKey,jdbcType=VARCHAR}
  and state = #{state,jdbcType=INTEGER}
  and crt_date >= #{beginDate,jdbcType=TIMESTAMP}
  and crt_date <= #{endDate,jdbcType=TIMESTAMP}

order by msg_id desc
